  const mainWrapper = useRef(null)
  const mainCircleRef = useRef(null)

  useEffect(() => {
    const { set, timeline, to } = gsap
    const fadeInTl = timeline()

    set(['.introText', '.headline', '.heroSection canvas', '.btn'], {
      opacity: 0,
    })

    set('.separator', { width: 0 })

    fadeInTl.to('.separator', {
      width: '80%',
      duration: 2,
      ease: 'power3.inOut',
    })

    fadeInTl.fromTo(
      '.introText',
      { opacity: 0, y: 10 },
      { opacity: 1, duration: 1, y: 0, ease: 'power3.out' }
    )

    fadeInTl.fromTo(
      '.headline',
      { opacity: 0, y: -10 },
      { opacity: 1, y: 0, duration: 1, ease: 'power4.out' },
      '-=1'
    )

    fadeInTl.fromTo(
      '.heroSection canvas',
      { opacity: 0 },
      { opacity: 1, duration: 1, ease: 'power4.out' }
    )

    fadeInTl.fromTo(
      '.heroSection .btn',
      { opacity: 0 },
      { opacity: 1, duration: 0.1, ease: 'power4.out' },
      '-=1'
    )

    const orbAnimationConfig = {
      motionPath: {
        alignOrigin: [0.5, 0.5],
        autoRotate: true,
      },
      ease: 'power1.inOut',
      repeat: -1,
      yoyo: true,
    }

    const orbs = [
      { selector: '.orb1', path: '.Path1', duration: 11 },
      { selector: '.orb2', path: '.Path2', duration: 19 },
      { selector: '.orb3', path: '.Path3', duration: 15 },
      { selector: '.orb4', path: '.Path4', duration: 15 },
      { selector: '.orb5', path: '.Path5', duration: 15 },
      { selector: '.orb6', path: '.Path6', duration: 20 },
      { selector: '.orb7', path: '.Path7', duration: 12 },
    ]

    orbs.forEach((orb) => {
      to(orb.selector, {
        ...orbAnimationConfig,
        motionPath: {
          ...orbAnimationConfig.motionPath,
          path: orb.path,
          align: orb.path,
        },
        duration: orb.duration,
      })
    })
  }, [])

